
public class CalculatorSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b = 5;
		char op = '+';
		switch (op) {
		    case '+': System.out.println(a + b); break;
		    case '-': System.out.println(a - b); break;
		    case '*': System.out.println(a * b); break;
		    case '/': System.out.println(a / b); break;
		}


	}

}
